import React, { Fragment } from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom';
import Moment from 'react-moment';
import { connect } from 'react-redux';
import { addLike, removeLike, deletePost } from '../../actions/post';

const PostItem = ({ auth, addLike, removeLike, deletePost, post: { _id, text, name, avatar, user, likes, comments
  , date, showActions } }) => {
  return (
    <div className="post bg-white p-1 my-1">
      <div>
        <Link to={`/profile/${user}`}>
          <img
            className="round-img"
            src={avatar}
            alt=""
          />
          <h4>{name}</h4>
        </Link>
      </div>
      <div>
        <p className="my-1">
          {text}
        </p>
        <p className="post-date">
          Posted on <Moment format="YYYY/MM/DD">{date}</Moment>
        </p>
        {showActions && (<Fragment>
          <button type="button" onClick={e => addLike(_id)} className="btn btn-light">
            <i className="fas fa-thumbs-up"></i>{' '}
            {likes.length > 0 && (
              <span>{likes.length}</span>
            )}

          </button>
          <button type="button" onClick={e => removeLike(_id)} className="btn btn-light">
            <i className="fas fa-thumbs-down"></i>
          </button>
          <Link to={`/posts/${_id}`} className="btn btn-primary">
            Discussion {comments.length > 0 && (
              <span className='comment-count'> {comments.length}</span>
            )}

          </Link>
          {!auth.loading && user === auth.user._id && (
            <button onClick={e => deletePost(_id)}
              type="button"
              className="btn btn-danger"
            >
              <i className="fas fa-times"></i>
            </button>
          )}
        </Fragment>
        )}


      </div>
    </div>
  )
}
PostItem.defaultProps = {
  showActions: true
}
PostItem.propTypes = {
  post: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
  addLike: PropTypes.func.isRequired,
  removeLike: PropTypes.func.isRequired,
  deletePost: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
  auth: state.authReducer
})

export default connect(mapStateToProps, { addLike, removeLike, deletePost })(PostItem)
